import React from "react";
import Timer from "../Timer";

function Countdown() {
  const countDownDate = new Date("Apr,9 2022 19:51:59").getTime();

  const [countdownTimer, setCountdownTimer] = React.useState(
    Timer(countDownDate)
  );

  React.useEffect(() => {
    const interval = setInterval(() => {
      setCountdownTimer(Timer(countDownDate));
    }, 1000);

    console.log(countdownTimer.distance);

    if (countdownTimer.distance < 0) {
      return () => {
        const now = new Date().getTime();
        clearInterval(interval);
        setCountdownTimer(Timer(now));
      };
    }
  });

  return (
    <div className="Countdown">
      <h1>New Year's Eve</h1>
      <hr />
      <div className="countdown-container">
        <div className="countdown-el days-c">
          <p className="big-text" id="days">
            {countdownTimer.days}
          </p>
          <span>days</span>
        </div>
        <div className="countdown-el hours-c">
          <p className="big-text" id="hours">
            {countdownTimer.hours}
          </p>
          <span>hours</span>
        </div>
        <div className="countdown-el minutes-c">
          <p className="big-text" id="minutes">
            {countdownTimer.minutes}
          </p>
          <span>minutes</span>
        </div>
        <div className="countdown-el seconds-c">
          <p className="big-text" id="seconds">
            {countdownTimer.seconds}
          </p>
          <span>seconds</span>
        </div>
      </div>
    </div>
  );
}

export default Countdown;
